import React from 'react';
import './collection.styles.scss';
import {connect} from 'react-redux';
import {createStructuredSelector}from 'reselect';
import { selectedCollection ,selectedShopItemsIsLoading }from '../../redux/shop/shop.selector';
import CollectionItem from '../../components/collection-item/collection-item.component';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionPage = ({match,collection})=>{

    const{title,items} = collection;
  //  console.log(title,items);
   return (
       <div className="collection-page">
       <div className="title">{title}</div>
           <div className="items">
           { items.map(item => <CollectionItem id={item.id} item={item}/>)}
           </div>

       </div> 
)}
/**
 *    高阶函数，
 */
const mapStateToProps = (state,ownProps) =>({
    collection:selectedCollection(ownProps.match.params.categoryId)(state),
    isLoading:selectedShopItemsIsLoading(state)
})


export default WithSpinner(connect(mapStateToProps)(CollectionPage))(this.props.isLoading,this.props.collection);
