import { createSelector } from 'reselect';

const selectCart = state => state.cart;

export const selectCartItems = createSelector(
    [selectCart], 
    cart=> cart.cartItems
)

export const selectCartItemsCount = selectCartItems().reducer((acc,item)=> acc+item.count, 0);
