import React, { Component } from 'react';
import CollectionPreview from '../../components/collection-preview/collection-preview';
import { connect} from 'react-redux';
import { initialShopItem} from '../../redux/shop/shop.actions';

 class ShopPage extends Component {

    componentDidMount(){
        this.props.initialShopItem();
    }

    render() {
        console.log()
        return (
            this.props.collections? (
                <div>
                {this.props.collections.map(c => (<CollectionPreview key={c.id} title ={c.title} items={c.items}/>))}
              </div>
            ):(<div>loading...</div>)

        )
    }
}

const mapStateToProps =(state)=>({
    collections:state.shopItems
})

export default connect(mapStateToProps,{initialShopItem})(ShopPage);
