import React from 'react';
import './collection.styles.scss';
import {connect} from 'react-redux';
import {createStructuredSelector}from 'reselect';
import { selectedCollection }from '../../redux/shop/shop.selector';
import CollectionItem from '../../components/collection-item/collection-item.component';

const CollectionPage = ({match,collection})=>{

    const category = match.params.categoryId;

   return (
    <div className="category-page">
        { collection.items.map(item => <CollectionItem id={item.id} item={item}/>)}
    </div>
)}

const mapStateToProps = category => createStructuredSelector({
    collection:selectedCollection(category)
})

export default connect(mapStateToProps)(CollectionPage);
