import { takeEvery} from 'redux-saga/effects';
import {ShopActionsDIC}  from './shop.types';
import firebase,{firestore,convertCollectionsSnapshotToMap , changeArrayToObject } from '../../firebase/firebase.util';

export function* fetchCollectionsAsync(){
  yield    console.log('I am fired');

  const collectionRef =firebase.firestore().collection('collections');


  collectionRef.get()
  .then(snapshot=> {
      const result = convertCollectionsSnapshotToMap(snapshot);
      const resultObject = changeArrayToObject(result);

  })
  .catch(error=> dispatch(fetchCollectionsFailure(error.message)));
}

export function* fetchCollectionsStart(){
    yield takeEvery(ShopActionsDIC.FETCH_COLLECTIONS_START, fetchCollectionsAsync);
}
