class Directory  extends React.Component{

    constructor(){
        super();
        this.state ={sections:[
            {},
            {},
            {}
        ]}
    }

    render(){
        return (
            <div className="directory-menu">
            {
                this.state.sections.map(item =><MenuItem title={item}/>)
            }
            </div>
        )
    }
}



export default Directory;
