import React from 'react';
import './checkout-item.style.scss';
import { increaseCount,decreaseCount,removeItem} from '../../redux/cart/cart.actions';
import {connect} from 'react-redux';

const CheckoutItem = ({increaseCount,decreaseCount,removeItem,item:{imageUrl,name,price,count}})=>{
    console.log(imageUrl,name,count,price);
    return(
    <div className="checkout-item">
        <div className="image-container">
        <img className="image" src={imageUrl} alt="item"/>
        </div>
        <span className="name">{name}</span>
         <span className="quantity">
         <span onClick={()=>increaseCount}>&#10094;</span>
         {count}
         <span onClick={()=>decreaseCount()}>&#10095;</span>
         </span>
         <span className="price">{price}</span>
        <span className="remove-button" onClick={()=>removeItem()}>&#10005;</span>


    </div>
)}

export default connect(null,{increaseCount,decreaseCount,removeItem}) (CheckoutItem);
