import { createSelector } from 'reselect';

const COLLECTION_ID_MAP ={
    hats:1,
    sneakers:2,

}
const selectShopItems = state => state.shop;

export const selectedShopItems = createSelector(
    [selectShopItems],
     shop => shop.shopItems
)
