import React from 'react';
import {connect} from 'react-redux';
import { createStructuredSelector }from 'reselect';
import { selectedShopItemsIsLoading} from '../../redux/shop/shop.selector';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionContainer = (ContaineredComponent,props) =>(OuterProps)=> {

        const WithSpinnerWappedComponent = WithSpinner(ContaineredComponent);


        return <WithSpinnerWappedComponent isLoading={!props.collectionIsLoaded} otherProps={OuterProps}/>

}   

const mapStateToProps = createStructuredSelector({
    collectionIsLoaded:selectedShopItemsIsLoading
});

export default connect(mapStateToProps)(CollectionContainer)
