import React from 'react';
import {Link} from 'react-router-dom';
import { ReactComponent as ShoppingIcon} from '../../asset/cart.svg';
import './cart-icon.style.scss';
import {connect} from 'react-redux';
import CartDropDown from '../cart-dropdown/car-dropdown.component';
import { getAllItems }from '../../redux/shop/shop.actions';

class CartIcon extends React.Component{

    componentDidMount(){
        this.props.getAllItems();
    }

    getCartItems =()=>{
        const cartItems = this.props.shop.cartItems;
        return (
            <div >
            {cartItems.map(item => <p key={item.id} >{item.name}</p>)}
            </div>
        );
    }



    render(){
        return (
            <div className="cart-icon" >
            <ShoppingIcon className="shopping-icon"/>

            <span className="item-count">{this.props.shop.cItemsCount}</span>


        </div>
        )
    }
}
const mapStateToProps = state =>({
    shop:state.shop
})

export default connect(mapStateToProps,{getAllItems})(CartIcon);
