                    handleChange={this.handleChange}
                    name="confirmPassword" 
                    type="password" 
                    value={this.state.confirmPassword} 
                    label="ConfirmPassword"
                    required />
                    <div className="buttons">
                        <CustomButton type='submit'>
                            Sign Up
                        </CustomButton>
                    </div>

                </form>
            </div>
        )
    }
}

export default SignUp;
