import React from 'react';
import './collection-item.style.scss';
import {connect} from 'react-redux';
import { addItemToCart } from '../../redux/shop/shop.actions';
import CustomButton from '../custom-button/custom-button.component';


class CollectionItem extends React.Component{

    addToCart = (id)=>{
        console.log(this.props.shop.shopItems);
        const routeName= this.props.routeName;
        const item = this.props.shop.shopItems.filter(item =>item.routeName === routeName).filter(item=>item.id === id);
        console.log('item',item);
        this.props.addItemToCart(item);
    }

    render(){
        const {id,name,price,imageUrl} = this.props;
        return (
            <div className="collection-item" onClick={ this.addToCart} >

            <div className="image"
                style={{backgroundImage:`url(${imageUrl})`}}
            >
                <div className="content"> 
                <span className="title">Add to Cart</span>
                </div>
            </div>

            <div className="collection-footer">
                <span className="name">{name}</span>
                <span className="price">{price}</span>
            </div>
          </div>

        );
    }
}
const mapStateToProps = state => ({
    shop:state.shop
})

export default connect(mapStateToProps,{addItemToCart })(CollectionItem);
