import React from 'react';
import './collection-shop.style.scss'
import {CollectionItem }from '../collection-item/collection-item.component';
import {connect} from 'react-redux';

//=({title,items}) 
class CollectionShop  extends React.Component {

    render(){
        const title="hats"
        const items = this.props.shopItems.filter(x=>x.routeName === title);
        console.log('items,',items[0].items);
        return (
            <div className="collection-preview">
            <h1>{title.toUpperCase()}</h1>
            <div className="preview">
                {items.items.map(({id,...otherProps})=>(<CollectionItem key={id} {...otherProps}/>))}
            </div>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    shopItems:state.shop.shopItems
})

export default connect(mapStateToProps)(CollectionShop);
