import { ShopActionsDIC } from './shop.types';
import SHOP_DATA  from '../../data/shop.data';

const initialShop ={
    shopItems:SHOP_DATA,
    cartItems:[],
    cCurrentItem:null,
    cItemsCount:0,
    cTotalPrice:0
}

 const shopReducer = (state = initialShop , action) => {
    switch (action.type) {
        case ShopActionsDIC.INT_SHOP_ITEMS:

        case ShopActionsDIC.ADD_ITEM_TO_CART: 
        return {
            ...state,
            shopItems:state.shopItems.map(item=>{
                if(item.id === action.payload.id){
                    return {
                        ...item,
                        count:1,
                        inCart:true
                    }
                }else {
                    return {
                        ...item,
                        count:0,
                        inCart:false
                    }
                }
            }),
            cartItems:[...state.cartItems,action.payload],
            cItemsCount:state.cCurrentItem +1,
            cTotalPrice: state.cTotalPrice + action.payload.price
        }
        case ShopActionsDIC.GET_ALL_ITEMS:
            return state;

        default:
            return state;
    }
}

export default shopReducer;
